import styled from "styled-components";
import { TodoItem } from "./types";


interface Props {
    todoItem: TodoItem;
    removeTodo(TodoNameDelete: string): void;
}

export const InputTodo = ({ todoItem, removeTodo}: Props) => {
    return (
        <table>
            <thead>
                <tr>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Content</th>
                    <th>Operate</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <RowTd>
                        {<input type="checkbox" 
                        // key={todo.id} onChange={(e) =>
                        //             {handleCheckbox(e, todo.id)}} 
                        />}
                    </RowTd>
                    <RowTd>
                    <span>{todoItem.desc}</span></RowTd>
                    <RowTd><span>{todoItem.category}</span></RowTd>
                    <RowTd><span>{todoItem.content}</span></RowTd>
                    <button onClick={() => {
                        removeTodo(todoItem.id);
                        }}
                    >
                        Delete
                    </button>
                </tr>
            </tbody>
        </table>
    );
};


const NavContainer = styled.div`

`
const TodoListContainer = styled.div`
    display:flex;
    flex-direction: column-reverse;
    color: rgb(0, 0, 0);
    padding-right:100px;
    padding-left:200px;
    border:aqua;
`
const RowTd = styled.td`
display: flex;
flex-direction: row;
margin: 4px auto;
color: rgb(0, 0, 0);
padding: 5px;
width: 30%;
text-align: center;
`
