import React, { useContext } from "react";
import { useForm, SubmitHandler } from "react-hook-form";
import { Redirect } from "react-router";
import styled from "styled-components";
import { TodoContext } from "./Context/TodoContext";

type Inputs = {
  email: string,
  password: string,
};


export const LoginPage: React.FC = () => { 
    const { 
        register, 
        handleSubmit, 
        formState: { errors } 
    } = useForm<Inputs>();

    const {login, authenticated } = useContext(TodoContext);

    const onSubmit = (data: any) => {
        console.log(data);
        login(data);
    };
    if (authenticated) {
        return <Redirect to="/" />
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <input {...register("email")} defaultValue="test" />
            <input
                {...register("password", { required: true, maxLength: 10 })}
            />
            {errors.password && <p>This field is required</p>}
            <input type="submit" />
        </form>
        // <FormContainer onSubmit={handleSubmit(onSubmit)}>
        //     <LoginHeader>
        //         Login
        //     </LoginHeader>
        //     <LoginText>Log into your account</LoginText>
        //     <Input type="text" placeholder="Enter your email" {...register("email")}/>
        //     <Input type="text" placeholder="Enter your password" {...register("password", { required: true })} />
        //         {errors.password && <span>This field is required</span>}
        //     <ButtonContainer>
        //         <ButtonInput type="submit" />
        //     </ButtonContainer>
        // </FormContainer>
    );
}
const FormContainer = styled.div`
