    margin: 10px auto;
    padding: 20px 0;
	background-image: linear-gradient(#A48EC9, #8473D9);    
    border-radius: 5px;

`
const LoginHeader = styled.div`
    padding: 10px 0;
    font-weight: bold;
    text-align: center;
    font-size: 18px;
    color: white;
`
const LoginText = styled.div`
    padding: 10px;
    font-size: 18px;
    color: white;
`
const Input = styled.input`
    font-size: 14px;
    padding: 10px;
    margin: 10px;
    background: white;
    border: none;
    border-radius: 3px;
    width: 90%;
`
const ButtonInput = styled.input`
    background-color: #4F49AF;
    width: 60%;
    height: 30px;
    color: white;
`
const ButtonContainer = styled.div`
    color: white;
    border: none;
    margin: 10px;
    padding: 10px 0;
    font-size: 16px;
    font-weight: 100;
    text-align: center;
`
