        setInputDesc(e.target.value);
    }

    const [inputCate, setInputCate] = useState([
        {Categoty: "aa"},
        {Categoty: "bb"},
    ]); 

    const [inputCont, setInputCont] = useState(['']); 

    const addTodo = todo => {
        setInputDesc(newInputDesc);

        const newTodoList = produce(this.state.todolist, draft => {
          draft.push({ no: new Date().getTime(), todo: todo, done: false });
        });
        this.setState({ todolist: newTodoList });
      };

      const values = {
        id:Math.floor(Math.random() * 1000),  //给todolist的事随机分配一个0-1000的数做id
        desc: inputDesc,
        category: inputCate,
        content: inputCont
      };

    return (
        <TodoContext.Provider value={values}>
            {inputDesc}
            {inputCate}
        </TodoContext.Provider>
    )
}

export default TodoContext;
