import React, {useState, useContext} from 'react';
import { Link } from 'react-router-dom';

import TodoContext from './Hooks/TodoContext';


function InputTodo() {
    const value = useContext(TodoContext);
    // debugger
//     const [inputDesc, setInputDesc] = useState([
// ]);

//     const [inputCate, setInputCate] = useState([]); 

//     const [inputCont, setInputCont] = useState(['']); 
    const [todo, setTodo] = useState("");

    return (
        <div>
            <div className='todo-name'>
                <div className='item-height'>
                    <input 
                        type='text'
                        name='text'
                        value={value.desc}
                        className='todo-input'
                        onChange={(e) => value.setInputDesc(e.target.value)}
                    />
                </div>

                <div className='item-height'>
                    <select
                        type='text'
                        value={value.category}
                        name='category'
                        className='todo-input'
                        onChange={(e) => value.setInputCate(e.target.value)}>
                        <option value=""></option>
                        <option value="CSS">CSS</option>
                        <option value="JS">JS</option>
                    </select>
                </div>

                <div className='item-height'>
                    <textarea 
                        type='text'
                        name='content'
                        input={value.content}
                        className='todo-input'
                        onChange={(e) => value.setInputCont(e.target.value)}
                    />
                </div>
                <div
                    onClick={() => {
                        value.addTodo(todo);
                        setTodo("");
                    }}>
                </div> 
            </div>
        </div>
    )
}

export default InputTodo;
