import { useContext } from "react";
import styled from "styled-components";
import { isTemplateExpression } from "typescript";
import { TodoContext } from "./Context/TodoContext";
import { TodoItem } from "./types";


interface Props {
    todoItem: TodoItem;
    removeTodo(TodoNameDelete: string): void;
}

export const InputTodo = ({ todoItem, removeTodo}: Props) => {
    const { handleCheck } = useContext(TodoContext);

    return (
        <tr>
            <th>
                {<input type="checkbox" 
                // key={todo.id} onChange={(e) =>
                //             {handleCheckbox(e, todo.id)}} 
                />}
                <input
                    onChange={() => handleCheck(todoItem.id, todoItem.checked)}
                    type="checkbox"
                    checked={todoItem.checked}
                />
            </th>
            <th><a href= {todoItem.content}>{todoItem.desc}</a></th>
            <th>{todoItem.category}</th>
            <th>{todoItem.content}</th>
            <DeleteButton onClick={() => {
                removeTodo(todoItem.id);
            }}>
                    Delete
            </DeleteButton>
        </tr>
)};

const DeleteButton = styled.a`
    margin: 10px;
    cursor: pointer;
    color: crimson;
`
