import { useContext } from "react";
import styled from "styled-components";
import { TodoContext } from "./Context/TodoContext";
import { TodoItem } from "./types";


interface Props {
    todoItem: TodoItem;
    removeTodo(TodoNameDelete: string): void;
}

export const InputTodo = ({ todoItem, removeTodo}: Props) => {
    const { handleCheck, todoCompleted } = useContext(TodoContext);

    return (
        <tr onClick={() => todoCompleted(todoItem.id, todoItem.done)}>
            <th>
                <input
                    onChange={() => handleCheck(todoItem.id, todoItem.checked)}
                    type="checkbox"
                    checked={todoItem.checked}
                />
            </th>
            <td>
                <a href= {`/todo/${todoItem.id}`}>{todoItem.desc}</a>
            </td>
            <InputTodoContainer done={todoItem.done} >{todoItem.category}</InputTodoContainer>
            <DeleteButton onClick={() => {
                removeTodo(todoItem.id);
            }}>
                Delete
            </DeleteButton>
        </tr>            
    );
};

interface StyledProps {
    done: boolean
}
const InputTodoContainer = styled.td<StyledProps>`
    color: ${props=> props.done ? "grey": "black"};
    text-decoration: ${props => props.done ?  "line-through" : "none"};
`
const DeleteButton = styled.div`
    margin: 10px;
    cursor: pointer;
    color: crimson;
`
