import React, { useContext } from "react";
import { useForm, SubmitHandler } from "react-hook-form";
import { Redirect } from "react-router";
import styled from "styled-components";
import { TodoContext } from "./Context/TodoContext";

type Inputs = {
  email: string,
  password: string,
};


export const LoginPage: React.FC = () => { 
    const { 
        register, 
        handleSubmit, 
        formState: { errors } 
    } = useForm<Inputs>();

    const {login, authenticated } = useContext(TodoContext);

    const onSubmit = (data: any) => {
        console.log(data);
        login(data);
    };
    if (authenticated) {
        return <Redirect to="/" />
    }

    return (
        // <form onSubmit={handleSubmit(onSubmit)}>
        //     <input {...register("email")} defaultValue="test" />
        //     <input
        //         {...register("password", { required: true, maxLength: 10 })}
        //     />
        //     {errors.password && <p>This field is required</p>}
        //     <input type="submit" />
        // </form>
        <FormContainer onSubmit={handleSubmit(onSubmit)}>
            <LoginHeader>
                Login
            </LoginHeader>
            <LoginText>Log into your account</LoginText>
            <Input type="text" placeholder="Enter your email" {...register("email")}/>
            <Input type="text" placeholder="Enter your password" {...register("password", { required: true })} />
                {errors.password && <span>This field is required</span>}
            <ButtonContainer>
            <input type="submit" />            </ButtonContainer>
        </FormContainer>
    );
}
const FormContainer = styled.div`
    max-width: 500px;
    margin: 10px auto;
    padding: 20px 0;
	background-image: linear-gradient(#A48EC9, #8473D9);    
    border-radius: 5px;

`
const LoginHeader = styled.div`
    padding: 10px 0;
    font-weight: bold;
    text-align: center;
    font-size: 18px;
    color: white;
`
const LoginText = styled.div`
    padding: 10px;
    font-size: 18px;
    color: white;
`
const Input = styled.input`
    font-size: 14px;
    padding: 10px;
    margin: 10px;
    background: white;
    border: none;
    border-radius: 3px;
    width: 90%;
`
const ButtonInput = styled.input`
    background-color: #4F49AF;
    width: 60%;
    height: 30px;
    color: white;
`
const ButtonContainer = styled.div`
    color: white;
    border: none;
    margin: 10px;
    padding: 10px 0;
    font-size: 16px;
    font-weight: 100;
    text-align: center;
`
