import React, { useState } from 'react';
import Delete from '../Delete';
import Todo from './Todo';
import Form from './Form';




// export interface AllData {
// }

// export const mock: AllData = {

// }

// export const TodoContext = React.createContext(mock)

// interface TodoContextProviderProps {
//     children: object
// }



function TodoContext (props) {


    const [inputDesc, setInputDesc] = useState([
        {Description: 1},
        {Description: 2}
    ]);
    const handleChangeDesc = e => {
        setInputDesc(e.target.value);
    }

    const [inputCate, setInputCate] = useState([
        {Categoty: "aa"},
        {Categoty: "bb"},
    ]); 

    const [inputCont, setInputCont] = useState(['']); 


    const [todos, setTodos] = useState([]);

    const addTodo = todo => {
        const newTodos = [todo, ...todos];
        setTodos(newTodos);
    };

    const removeTodo = id => {
        const removeArr = [...todos].filter(todo => todo.id !== id);
        setTodos(removeArr);
    };

    const values = {
        id:Math.floor(Math.random() * 1000),  //给todolist的事随机分配一个0-1000的数做id
        desc: inputDesc,
        category: inputCate,
        content: inputCont
    };

    return (
        <TodoContext.Provider value={values}>
            {props.inputDesc}
            {props.inputCate}
            <Form onSubmit={addTodo} /> 
            <Delete />  
            <div  className='todo-column'>
                <Todo todos={todos} removeTodo={removeTodo}/>
            </div>
        </TodoContext.Provider>
    )
}

// const TodoContextConsumer = TodoContext.Consumer;
// export { TodoContext, TodoContextConsumer };
export default TodoContext;
