import React, { ChangeEvent, useState, useContext } from 'react';
import styled from "styled-components";
import { TodoContext } from './Context/TodoContext';

export const Form = () => {
    const { addTodo} = useContext(TodoContext)
    const [inputDesc, setInputDesc] = useState<string>('')
    const [inputCate, setInputCate] = useState<string>('')
    const [inputCont, setInputCont] = useState<string>('')
    // const [todoList, setTodoList] = useState<TodoItem[]>([]);


    const handleChangeDesc = (event: ChangeEvent<HTMLInputElement>) => {
        setInputDesc(event.target.value)
    };
    const handleChangeCate= (event: ChangeEvent<HTMLSelectElement>) => {
        setInputCate(event.target.value)
    };
    const handleChangeCont = (event: ChangeEvent<HTMLTextAreaElement>) => {
        setInputCont(event.target.value)
    };

    const onClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault();
        addTodo({
            id: Date.now().toString(), //现在的时间戳
            desc: inputDesc,
            category: inputCate,
            content: inputCont
        })
    }

    return (
        <div>
            <FormContainer> 
                <div>
                    <text> Description: </text>
                        <input 
                            type='text'
                            name='text'
                            value={inputDesc}
                            className='todo-input'
                            onChange={handleChangeDesc}
                        />
                </div>
                <div>
                    <text>Category:</text>
                    <select
                        value={inputCate}
                        name='category'
                        className='todo-input'
                        onChange={handleChangeCate}
                    >
                        <option value=""></option>
                        <option value="CSS">CSS</option>
                        <option value="JS">JS</option>
                    </select>
                </div>
                <div>
                    <text>Content:</text>
                    <textarea 
                        value={inputCont}
                        name='category'
                        className='todo-input'
                        onChange={handleChangeCont}
                    />
                </div>
                <button onClick={onClick}>Submit</button>
                <TodoListContainer>
                    {/* {todoList.map((todoItem: TodoItem) => {
                        return <InputTodo key={todoItem.id} todoItem={todoItem} removeTodo={removeTodo} />;
                    })} */}
                </TodoListContainer>
            </FormContainer>
        </div>

    );
}

const NavContainer = styled.div`

`
const FormContainer = styled.div`
    display:flex;
    float:left;
    flex-direction: column;
    width: 90px;
    padding-top: 15px;  
`
const TodoListContainer = styled.div`

`
