import styled from "styled-components";
import { TodoItem } from "./types";


interface Props {
    todoItem: TodoItem;
    removeTodo(TodoNameDelete: string): void;
}

export const InputTodo = ({ todoItem, removeTodo}: Props) => {
    return (
        <table>
            <thead>
                <tr>
                    <th>
                        Description
                        Category
                        Content
                        Operate
                    </th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>
                        {<input type="checkbox" 
                        // key={todo.id} onChange={(e) =>
                        //             {handleCheckbox(e, todo.id)}} 
                        />}
                    </td>
                    <td>
                    <span>{todoItem.desc}</span></td>
                    <td><span>{todoItem.category}</span></td>
                    <td><span>{todoItem.content}</span></td>
                    <button onClick={() => {
                        removeTodo(todoItem.id);
                        }}
                    >
                        Delete
                    </button>
                </tr>
            </tbody>
        </table>
    );
};


const NavContainer = styled.div`

`
const TodoListContainer = styled.div`
    display:flex;
    flex-direction: column-reverse;
    color: rgb(0, 0, 0);
    padding-right:100px;
    padding-left:200px;
    border:aqua;
`
