import { useContext } from "react";
import styled from "styled-components";
import { TodoContext } from "./Context/TodoContext";
import { TodoItem } from "./types";


interface Props {
    todoItem: TodoItem;
    removeTodo(TodoNameDelete: string): void;
}

export const InputTodo = ({ todoItem, removeTodo}: Props) => {
    const { handleCheck, todoCompleted } = useContext(TodoContext);

    return (
        <div>
            {todoItem.done ? (
                <TodoCompletedTr onClick={() => todoCompleted(todoItem.id, todoItem.done)}>
                    <th>
                        <input
                            onChange={() => handleCheck(todoItem.id, todoItem.checked)}
                            type="checkbox"
                            checked={todoItem.checked}
                        />
                    </th>
                    <td>
                        <a href= {`/todo/${todoItem.id}`}>{todoItem.desc}</a>
                    </td>
                    <td>{todoItem.category}</td>
                    <DeleteButton onClick={() => {
                        removeTodo(todoItem.id);
                    }}>
                        Delete
                    </DeleteButton>
                </TodoCompletedTr>

            ): (
                <tr>
                    <InputTodoTr onClick={() => todoCompleted(todoItem.id, todoItem.done)}>
                        <th>
                            <input
                                onChange={() => handleCheck(todoItem.id, todoItem.checked)}
                                type="checkbox"
                                checked={todoItem.checked}
                            />
                        </th>
                        <td>
                            <a href= {`/todo/${todoItem.id}`}>{todoItem.desc}</a>
                        </td>
                        <td>{todoItem.category}</td>
                    </InputTodoTr>
                    <DeleteButton onClick={() => {
                        removeTodo(todoItem.id);
                    }}>
                        Delete
                    </DeleteButton>
                </tr>
            )}
    </div>
            // <InputTodoTr onClick={() => todoCompleted(todoItem.id, todoItem.done)}>
            //     <th>
            //         <input
            //             onChange={() => handleCheck(todoItem.id, todoItem.checked)}
            //             type="checkbox"
            //             checked={todoItem.checked}
            //         />
            //     </th>
            //     <td>
            //         <a href= {`/todo/${todoItem.id}`}>{todoItem.desc}</a>
            //     </td>
            //     <td>{todoItem.category}</td>
            //     <DeleteButton onClick={() => {
            //         removeTodo(todoItem.id);
            //     }}>
            //         Delete
            //     </DeleteButton>
            // </InputTodoTr> 
    );
};


const DeleteButton = styled.a`
    margin: 10px;
    cursor: pointer;
    color: crimson;
`
const InputTodoTr = styled.tr`
    color: black;
`
const TodoCompletedTr  = styled.tr`
    color: grey;
    text-decoration: line-through;
`
